/**
 * 环境相关
 */

var HOST = window.location.host;

var envFlag = 'online';
var envFrom = 'pc';

if (HOST.indexOf('.taobao.com') != -1) {
  envFrom = 'h5';
}

if (HOST.indexOf('daily.taobao.net') != -1 || HOST.indexOf('waptest.taobao.com') != -1) {
  envFlag = 'daily';
} else if (HOST.indexOf('wapa.taobao.com') != -1) {
  envFlag = 'pre';
}


exports.envFlag = envFlag;

exports.getPaimaiPCHost = function(migrated) {
  var hostPrefix = migrated ? 'item-paimai' : 'paimai';
  if (envFlag == 'daily') {
    return hostPrefix + '.daily.taobao.net';
  } else {
    return hostPrefix + '.taobao.com';
  }
};

exports.getTaoMobileHost = function() {
  switch (envFlag) {
    case 'daily':
      return 'h5.waptest.taobao.com';
    case 'pre':
      return 'h5.wapa.taobao.com';
    default:
      return 'h5.m.taobao.com';
  }
};