/**
 * 二手车相关
 */

/**
 * 格式化公里数
 * @param  {String} miles 公里数
 * @return {String}       格式化后的公里数
 */
exports.formatMils = function(miles) {
  if (!miles) {
    return '';
  }
  if (miles.indexOf('公里') != -1) {
    return miles;
  }
  if (miles.length >= 4) {
    return (new Number(miles / 10000)).toFixed(1) + '万公里';
  }
  if (miles) {
    return miles + '公里';
  }
};