/** @jsx createElement */
import { cloneMerge, buffer, isTrue, unEscapeHTML, versionCompare, arrayChunk, isTrueForTF } from '../index';

/** 测试 cloneMerge */
const defaultObject = {
  a: {
    a1: 1,
    a2: 2
  },
  b: {
    b1: 1
  }
};
const a1 = cloneMerge(defaultObject, {
  a: {
    a1: 2,
    a3: 3
  },
  c: {
    a: 1
  }
});
const a2 = cloneMerge(defaultObject, {
  a: {
    a1: 3,
    a3: 4
  }
});
console.log('Test cloneMerge:', a1, a2);


/** 测试 buffer */
const bufferFn = buffer(function(i) {
  console.log('Test buffer:', i);
}, 1000);

bufferFn(1);
bufferFn(2);


/** 测试 isTrue */
console.log('Test isTrue:', isTrue('true'));

/** 测试 isTrueForTF */
console.log('Test isTrueForTF:', isTrueForTF('1'));

/** 测试 unEscapeHTML */
console.log('Test unEscapeHTML:', unEscapeHTML('&lt;span&gt;我是span&nbsp;&mdash;&middot;&ldquo;啦啦&rdquo;&lt;span&gt;'));

/** 测试 versionCompare */
console.log('Test versionCompare:', versionCompare('6.10.2', '8.0.0'));

/** 测试 arrayChunk */
console.log('Test arrayChunk:', arrayChunk([0, 1, 2, 3, 4, 5, 6], 3));