export default function(originalArr, num = 2) {
  if (!Array.isArray(originalArr) || typeof num !== 'number') {
    return originalArr;
  }
  const originCopy = [].concat(originalArr); // 不影响 originalArr
  const columns = [];
  while (originCopy.length > 0) {
    columns.push(originCopy.splice(0, num));
  }
  return columns;
};
